func partOne(s string) int {
	floorNumber := 0
	for _, floor := range s {
		if string(floor) == "(" {
			floorNumber++
		} else if string(floor) == ")" {
			floorNumber--
		}
	}

	return floorNumber
}

func partTwo(s string) int {
	floorNumber := 0
	for i, floor := range s {
		if floorNumber < 0 {
			return i
		}

		if string(floor) == "(" {
			floorNumber++
		} else {
			floorNumber--
		}
	}
	return floorNumber
}
