def partOne(info):
  floorNumber = 0
  for rule in info:
    if rule == "(":
      floorNumber += 1
    else:
      floorNumber -= 1
  print(floorNumber)

def partTwo(info):
  floorNumber = 0
  for i in range(len(info)):
    if floorNumber < 0:
      return print(i)

    if info[i] == "(":
      floorNumber += 1
    elif info[i]:
      floorNumber -= 1
    
  
  print(floorNumber)


